var mongojs = require('mongojs');

module.exports = new function () {
	$ = require('nodeigniter');
	var cnf = $.config(':database');
	var connection_string = (cnf.use_auth?(cnf.username + ':' + cnf.password + '@'):'') + cnf.host + ':' + cnf.port + '/' + cnf.name;
	this.db = mongojs(connection_string, cnf.collections);
}
